mapper.select:
SELECT FILE_ID AS fileId, APP_CODE AS appCode, FILE_URL AS fileUrl, FILE_ORIGIN_NAME AS fileOriginName, FILE_SUFFIX AS fileSuffix, FILE_SIZE AS fileSize, FILE_STORAGE_NAME AS fileStorageName, CREATE_TIME AS createTime, UPDATE_TIME AS updateTime FROM sys_fileinfo where 1 = 1
AND APP_CODE = #{appCode}
AND FILE_ORIGIN_NAME LIKE CONCAT('%',#{fileOriginName},'%')
AND FILE_SUFFIX = #{fileSuffix}
AND FILE_ID = #{fileId}
