mapper.select:
SELECT
ID AS id, APP_CODE AS appCode, IP AS ip, SERVLET_PATH AS servletPath, RPC_PHASE AS rpcPhase, TRACE_ID AS
traceId,
SPAN_ID AS spanId, PARENT_SPAN_ID AS parentSpanId, CREATE_TIMESTAMP AS createTimestamp
from log_trace_log
where 1 = 1

    and TRACE_ID = #{traceId}


    and `APP_CODE` = #{appCode}


    and RPC_PHASE = #{rpcPhase}


    and ( CREATE_TIMESTAMP >= #{beginTime} and CREATE_TIMESTAMP <= #{endTime} )

ORDER BY ID desc LIMIT #{limitOffset}, #{pageSize};

SELECT ID AS id, APP_CODE AS appCode, IP AS ip, SERVLET_PATH AS servletPath, RPC_PHASE AS rpcPhase, TRACE_ID AS traceId, SPAN_ID AS spanId, PARENT_SPAN_ID AS parentSpanId, CREATE_TIMESTAMP AS createTimestamp from log_trace_log where 1 = 1
and TRACE_ID = #{traceId}
and `APP_CODE` = #{appCode}
and RPC_PHASE = #{rpcPhase}
and ( CREATE_TIMESTAMP >= #{beginTime} and CREATE_TIMESTAMP <= #{endTime} )
ORDER BY ID desc
