mapper.select:
SELECT user.user_id AS userId, comp.`name` AS companyName, dept.dept_name AS deptName, duty.duty_name AS dutyName, user.name AS userName, CASE user.sex WHEN "M" THEN "男" WHEN "F" THEN "女" end AS sex, act.account AS account, user.`status` FROM biemo_ent_user_dept ud INNER JOIN biemo_ent_user user ON ud.user_id = user.user_id INNER JOIN biemo_ent_dept dept ON dept.dept_id = ud.dept_id INNER JOIN biemo_ent_company comp ON comp.company_id = dept.company_id INNER JOIN biemo_ent_duty duty ON duty.duty_id = ud.duty_id INNER JOIN biemo_ent_user_account act ON user.user_id = act.user_id WHERE ud.default_flag = 'Y'
AND dept.company_id = #{paramCondition.companyId}
AND dept.dept_id = #{paramCondition.deptId}
AND user.name like concat('%',#{paramCondition.name},'%')
AND act.account like concat('%',#{paramCondition.account},'%')
ORDER BY user.order_no asc
