mapper.select:
SELECT n.permission_id AS permissionId, n.app_id AS appId, n.permission_name AS permissionName, n.permission_desc AS permissionDesc, n.parent_id AS parentId, n.order_no AS orderNo, n.status AS status, m.permission_name as parentName, p.app_name AS appName FROM biemo_sys_permission n INNER JOIN biemo_sys_app p ON n.app_id = p.APP_ID LEFT JOIN biemo_sys_permission m ON n.parent_id = m.permission_id WHERE 1 = 1
and n.app_id = #{param.appId}
and n.parent_id = #{param.parentId}
and n.status = #{param.status}
and n.PERMISSION_NAME like CONCAT('%',#{param.permissionName},'%')
AND n.del_flag = #{param.delFlag} ORDER BY n.app_id ASC, n.parent_id ASC ,n.create_time ASC

SELECT n.permission_id AS permissionId, n.app_id AS appId, n.permission_name AS permissionName, n.permission_desc AS permissionDesc, n.parent_id AS parentId, n.order_no AS orderNo, n.status AS status, m.permission_name as parentName, p.app_name AS appName FROM biemo_sys_permission n INNER JOIN biemo_sys_app p ON n.app_id = p.APP_ID LEFT JOIN biemo_sys_permission m ON n.parent_id = m.permission_id WHERE 1 = 1
and n.app_id = #{appId}
and n.status = #{status}
AND n.del_flag = #{delFlag}

SELECT app.APP_ID AS permissionId, app.APP_ID AS appId, app.APP_NAME AS permissionName, '-1' AS parentId FROM biemo_sys_app app WHERE 1=1
and app.APP_ID = #{appId}
UNION SELECT pm.PERMISSION_ID AS permissionId, pm.APP_ID AS appId, pm.PERMISSION_NAME AS permissionName, if(PARENT_ID='-1', APP_ID, PARENT_ID) AS parentId FROM biemo_sys_permission pm LEFT JOIN biemo_sys_role_permission role_pm on role_pm.PERMISSION_ID = pm.PERMISSION_ID WHERE 1=1
and pm.APP_ID = #{appId}
and pm.APP_ID = #{appId}
and pm.DEL_FLAG = #{delFlag}
and pm.STATUS = #{status}

SELECT
pm.PERMISSION_ID AS permissionId,
pm.APP_ID AS appId,
pm.PERMISSION_NAME AS permissionName,
if(PARENT_ID='-1', APP_ID, PARENT_ID) AS parentId,
if(role_pm.ROLE_ID = #{roleId}, 1, 0) as checked
FROM
biemo_sys_permission pm
LEFT JOIN biemo_sys_role_permission role_pm on
role_pm.PERMISSION_ID = pm.PERMISSION_ID and role_pm.ROLE_ID = #{roleId}
WHERE
1=1

    and pm.APP_ID = #{appId}


    and pm.DEL_FLAG = #{delFlag}


    and pm.STATUS = #{status}
