mapper.select:
SELECT role.ROLE_ID AS roleId, role.ROLE_CODE AS roleCode, role.ROLE_NAME AS roleName, role.ROLE_DESC AS roleDesc, role.APP_ID AS appId, app.APP_NAME AS appName, role.CREATE_TIME AS createTime, role.CREATE_USER AS createUser, role.UPDATE_TIME AS updateTime, role.UPDATE_USER AS updateUser, role.STATUS AS status, role.DEL_FLAG AS delFlag FROM biemo_sys_role role LEFT JOIN biemo_sys_app app on app.APP_ID = role.APP_ID WHERE 1 = 1
and role.ROLE_ID = #{param.roleId}
and role.ROLE_NAME LIKE concat('%', #{param.roleName}, '%')
and role.ROLE_CODE = #{param.roleCode}
and role.APP_ID = #{param.appId}
and role.DEL_FLAG = #{param.delFlag}
ORDER BY app.ORDER_NO ASC, role.ORDER_NO ASC, role.ROLE_NAME DESC

SELECT role.role_id AS "roleId", role.role_code AS "roleCode", role.role_name AS "roleName", role.role_desc AS "roleDesc", role.app_id AS "appId", app.app_name AS "appName", role.order_no AS "orderNo", role.status AS "status", role.del_flag AS "delFlag" FROM biemo_sys_role role LEFT JOIN biemo_sys_app app on app.APP_ID = role.APP_ID WHERE 1 = 1
and role.ROLE_ID = #{roleId}
and role.ROLE_NAME = #{roleName}
and role.ROLE_CODE = #{roleCode}
and role.APP_ID = #{appId}
and role.DEL_FLAG = #{delFlag}

