mapper.select:
SELECT uu.*,GROUP_CONCAT(uu.userName) AS names ,GROUP_CONCAT(uu.infoId) AS infoIds from ( SELECT DISTINCT p.APP_NAME AS appName, n.ROLE_ID AS roleId, n.ROLE_CODE AS roleCode, n.ROLE_NAME AS roleName, n.ROLE_DESC AS roleDesc, n.APP_ID AS appId, n.STATUS AS roleStatus, p.INFO_ID AS infoId, p.`NAME` AS username, p.ORDER_NO AS orderNo, n.ORDER_NO AS rOrderNo FROM biemo_sys_role n INNER JOIN biemo_sys_app p ON n.APP_ID = p.APP_ID LEFT JOIN biemo_sys_user_role m ON n.ROLE_ID = m.ROLE_ID Left JOIN (SELECT DISTINCT a.ACCOUNT_ID ,u.INFO_ID,u.`NAME` FROM biemo_ent_user_account a INNER JOIN biemo_ent_user_info u ON a.INFO_ID = u.INFO_ID where a.COMPANY_ID = #{companyId}) p on m.ACCOUNT_ID = p.ACCOUNT_ID WHERE 1 = 1
AND n.ROLE_NAME LIKE CONCAT('%',#{roleName},'%')
AND n.ROLE_TYPE = #{roleType}
AND n.BLOC_FLAG IN
#{item}
AND n.HIDE_FLAG = #{hideFlag}
AND n.STATUS = #{status}
AND n.DEL_FLAG = #{delFlag}
AND n.APP_ID IN
#{item}
AND n.MGR_FLAG IN
#{item}
) uu GROUP BY uu.roleId ORDER BY uu.orderNo,uu.rOrderNo ASC
